package com.dh.main;

public class WatchaNextWatcha {
	private int no;
	private String img;
	private String coment;
	
	public WatchaNextWatcha() {
		// TODO Auto-generated constructor stub
	}
	public int getNo() {
		return no;
	}
	public void setNo(int no) {
		this.no = no;
	}
	public String getImg() {
		return img;
	}
	public void setImg(String img) {
		this.img = img;
	}
	public String getComent() {
		return coment;
	}
	public void setComent(String coment) {
		this.coment = coment;
	}
	public WatchaNextWatcha(int no, String img, String coment) {
		super();
		this.no = no;
		this.img = img;
		this.coment = coment;
	}
	
	
}
